mapper.select:
select 

from tbdiscountitem
where fiItemCd = #{fiItemCd,jdbcType=INTEGER}
  and fiOrderUintCd = #{fiOrderUintCd,jdbcType=INTEGER}
  and fsDiscountId = #{fsDiscountId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.delete:
delete from tbdiscountitem
where fiItemCd = #{fiItemCd,jdbcType=INTEGER}
  and fiOrderUintCd = #{fiOrderUintCd,jdbcType=INTEGER}
  and fsDiscountId = #{fsDiscountId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.insert:
insert into tbdiscountitem (fiItemCd, fiOrderUintCd, fsDiscountId, 
  fsShopGUID, fiDiscountRate, fsUpdateTime, 
  fsUpdateUserId, fsUpdateUserName, fiStatus
  )
values (#{fiItemCd,jdbcType=INTEGER}, #{fiOrderUintCd,jdbcType=INTEGER}, #{fsDiscountId,jdbcType=VARCHAR}, 
  #{fsShopGUID,jdbcType=VARCHAR}, #{fiDiscountRate,jdbcType=INTEGER}, #{fsUpdateTime,jdbcType=VARCHAR}, 
  #{fsUpdateUserId,jdbcType=VARCHAR}, #{fsUpdateUserName,jdbcType=VARCHAR}, #{fiStatus,jdbcType=INTEGER}
  )

mapper.update:
update tbdiscountitem


    fiDiscountRate = #{fiDiscountRate,jdbcType=INTEGER},


    fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},


    fsUpdateUserId = #{fsUpdateUserId,jdbcType=VARCHAR},


    fsUpdateUserName = #{fsUpdateUserName,jdbcType=VARCHAR},


    fiStatus = #{fiStatus,jdbcType=INTEGER},


where fiItemCd = #{fiItemCd,jdbcType=INTEGER}
  and fiOrderUintCd = #{fiOrderUintCd,jdbcType=INTEGER}
  and fsDiscountId = #{fsDiscountId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

update tbdiscountitem
set fiDiscountRate = #{fiDiscountRate,jdbcType=INTEGER},
  fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsUpdateUserId,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsUpdateUserName,jdbcType=VARCHAR},
  fiStatus = #{fiStatus,jdbcType=INTEGER}
where fiItemCd = #{fiItemCd,jdbcType=INTEGER}
  and fiOrderUintCd = #{fiOrderUintCd,jdbcType=INTEGER}
  and fsDiscountId = #{fsDiscountId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}
