mapper.select:
select 

from tbpaymentfullcut
where fsPaymentId = #{fsPaymentId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.delete:
delete from tbpaymentfullcut
where fsPaymentId = #{fsPaymentId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.update:
update tbpaymentfullcut
set fdFullmoney = #{fdFullmoney,jdbcType=DECIMAL},
  fdCutmoney = #{fdCutmoney,jdbcType=DECIMAL},
  fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsUpdateUserId,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsUpdateUserName,jdbcType=VARCHAR}
where fsPaymentId = #{fsPaymentId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}
