mapper.select:
select 

from tbreportconfiguration
where fsConfigurationId = #{fsConfigurationId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.delete:
delete from tbreportconfiguration
where fsConfigurationId = #{fsConfigurationId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.insert:
insert into tbreportconfiguration (fsConfigurationId, fsShopGUID, fsConfigurationName, 
  fsConfigurationValue, fsFoodTradeId, fiStatus, 
  fsUpdateTime, fsUpdateUserId, fsUpdateUserName
  )
values (#{fsConfigurationId,jdbcType=VARCHAR}, #{fsShopGUID,jdbcType=VARCHAR}, #{fsConfigurationName,jdbcType=VARCHAR}, 
  #{fsConfigurationValue,jdbcType=VARCHAR}, #{fsFoodTradeId,jdbcType=VARCHAR}, #{fiStatus,jdbcType=INTEGER}, 
  #{fsUpdateTime,jdbcType=VARCHAR}, #{fsUpdateUserId,jdbcType=VARCHAR}, #{fsUpdateUserName,jdbcType=VARCHAR}
  )

mapper.update:
update tbreportconfiguration
set fsConfigurationName = #{fsConfigurationName,jdbcType=VARCHAR},
  fsConfigurationValue = #{fsConfigurationValue,jdbcType=VARCHAR},
  fsFoodTradeId = #{fsFoodTradeId,jdbcType=VARCHAR},
  fiStatus = #{fiStatus,jdbcType=INTEGER},
  fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsUpdateUserId,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsUpdateUserName,jdbcType=VARCHAR}
where fsConfigurationId = #{fsConfigurationId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}
