mapper.select:
select 

from tbreporttemplet
where fsReportId = #{fsReportId,jdbcType=VARCHAR}
  and fsReportTempletId = #{fsReportTempletId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.delete:
delete from tbreporttemplet
where fsReportId = #{fsReportId,jdbcType=VARCHAR}
  and fsReportTempletId = #{fsReportTempletId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.update:
update tbreporttemplet
set fsReportTempletName = #{fsReportTempletName,jdbcType=VARCHAR},
  fsReportTempletDesc = #{fsReportTempletDesc,jdbcType=VARCHAR},
  fiPaperSize = #{fiPaperSize,jdbcType=INTEGER},
  fsRptFile = #{fsRptFile,jdbcType=VARCHAR},
  fiStatus = #{fiStatus,jdbcType=INTEGER},
  fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsUpdateUserId,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsUpdateUserName,jdbcType=VARCHAR},
  fsFoodTradeId = #{fsFoodTradeId,jdbcType=VARCHAR}
where fsReportId = #{fsReportId,jdbcType=VARCHAR}
  and fsReportTempletId = #{fsReportTempletId,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}
