mapper.select:
select 

from tbsellhurry
where fiHurrySeq = #{fiHurrySeq,jdbcType=INTEGER}
  and fsSellNo = #{fsSellNo,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.delete:
delete from tbsellhurry
where fiHurrySeq = #{fiHurrySeq,jdbcType=INTEGER}
  and fsSellNo = #{fsSellNo,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.update:
update tbsellhurry


    fsCreateTime = #{fsCreateTime,jdbcType=VARCHAR},


    fsCreateUserName = #{fsCreateUserName,jdbcType=VARCHAR},


where fiHurrySeq = #{fiHurrySeq,jdbcType=INTEGER}
  and fsSellNo = #{fsSellNo,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

update tbsellhurry
set fsCreateTime = #{fsCreateTime,jdbcType=VARCHAR},
  fsCreateUserName = #{fsCreateUserName,jdbcType=VARCHAR}
where fiHurrySeq = #{fiHurrySeq,jdbcType=INTEGER}
  and fsSellNo = #{fsSellNo,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}
