mapper.select:
select 

from tbseqno
where fsCls = #{fsCls,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.delete:
delete from tbseqno
where fsCls = #{fsCls,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

mapper.update:
update tbseqno


    fiSeqNo = #{fiSeqNo,jdbcType=INTEGER},


    fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},


    lver = #{lver,jdbcType=INTEGER},


where fsCls = #{fsCls,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}

update tbseqno
set fiSeqNo = #{fiSeqNo,jdbcType=INTEGER},
  fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},
  lver = #{lver,jdbcType=INTEGER}
where fsCls = #{fsCls,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}
