mapper.select:
select 

from tbtableinfo
where fsKind = #{fsKind,jdbcType=VARCHAR}
  and fsTableId = #{fsTableId,jdbcType=VARCHAR}
  and fsTableName = #{fsTableName,jdbcType=VARCHAR}

mapper.delete:
delete from tbtableinfo
where fsKind = #{fsKind,jdbcType=VARCHAR}
  and fsTableId = #{fsTableId,jdbcType=VARCHAR}
  and fsTableName = #{fsTableName,jdbcType=VARCHAR}

mapper.update:
update tbtableinfo


    fiSortOrder = #{fiSortOrder,jdbcType=INTEGER},


    fsFoodTradeList = #{fsFoodTradeList,jdbcType=VARCHAR},


where fsKind = #{fsKind,jdbcType=VARCHAR}
  and fsTableId = #{fsTableId,jdbcType=VARCHAR}
  and fsTableName = #{fsTableName,jdbcType=VARCHAR}

update tbtableinfo
set fiSortOrder = #{fiSortOrder,jdbcType=INTEGER},
  fsFoodTradeList = #{fsFoodTradeList,jdbcType=VARCHAR}
where fsKind = #{fsKind,jdbcType=VARCHAR}
  and fsTableId = #{fsTableId,jdbcType=VARCHAR}
  and fsTableName = #{fsTableName,jdbcType=VARCHAR}
