mapper.select:
select 

from tbtrade
where fsCompanyGUID = #{fsCompanyGUID,jdbcType=VARCHAR}
  and fsTradeId = #{fsTradeId,jdbcType=VARCHAR}

mapper.delete:
delete from tbtrade
where fsCompanyGUID = #{fsCompanyGUID,jdbcType=VARCHAR}
  and fsTradeId = #{fsTradeId,jdbcType=VARCHAR}

mapper.update:
update tbtrade
set fsTradeName = #{fsTradeName,jdbcType=VARCHAR},
  fiStatus = #{fiStatus,jdbcType=INTEGER},
  fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsUpdateUserId,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsUpdateUserName,jdbcType=VARCHAR}
where fsCompanyGUID = #{fsCompanyGUID,jdbcType=VARCHAR}
  and fsTradeId = #{fsTradeId,jdbcType=VARCHAR}
