mapper.select:
select 

from tbuserauthdata
where fsDataKind = #{fsDataKind,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}
  and fsUserId = #{fsUserId,jdbcType=VARCHAR}
  and fsValue = #{fsValue,jdbcType=VARCHAR}

mapper.delete:
delete from tbuserauthdata
where fsDataKind = #{fsDataKind,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}
  and fsUserId = #{fsUserId,jdbcType=VARCHAR}
  and fsValue = #{fsValue,jdbcType=VARCHAR}

mapper.insert:
