  fsValue, fsUpdateTime, fiStatus
  )
values (#{fsDataKind,jdbcType=VARCHAR}, #{fsShopGUID,jdbcType=VARCHAR}, #{fsUserId,jdbcType=VARCHAR}, 
  #{fsValue,jdbcType=VARCHAR}, #{fsUpdateTime,jdbcType=VARCHAR}, #{fiStatus,jdbcType=INTEGER}
  )

mapper.update:
update tbuserauthdata


    fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},


    fiStatus = #{fiStatus,jdbcType=INTEGER},


where fsDataKind = #{fsDataKind,jdbcType=VARCHAR}
  and fsShopGUID = #{fsShopGUID,jdbcType=VARCHAR}
  and fsUserId = #{fsUserId,jdbcType=VARCHAR}
  and fsValue = #{fsValue,jdbcType=VARCHAR}

update tbuserauthdata
set fsUpdateTime = #{fsUpdateTime,jdbcType=VARCHAR},
  fiStatus = #{fiStatus,jdbcType=INTEGER}
