mapper.select:
select 

from tbauthority
where fsProgId = #{fsprogid,jdbcType=VARCHAR}
  and fsProjId = #{fsprojid,jdbcType=VARCHAR}
  and fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbauthority
where fsProgId = #{fsprogid,jdbcType=VARCHAR}
  and fsProjId = #{fsprojid,jdbcType=VARCHAR}
  and fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.insert:
insert into tbauthority (fsProgId, fsProjId, fsRoleId, 
  fsShopGUID, fsUpdateTime, fiStatus
  )
values (#{fsprogid,jdbcType=VARCHAR}, #{fsprojid,jdbcType=VARCHAR}, #{fsroleid,jdbcType=VARCHAR}, 
  #{fsshopguid,jdbcType=VARCHAR}, #{fsupdatetime,jdbcType=VARCHAR}, #{fistatus,jdbcType=INTEGER}
  )

mapper.update:
update tbauthority


    fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},


    fiStatus = #{fistatus,jdbcType=INTEGER},


where fsProgId = #{fsprogid,jdbcType=VARCHAR}
  and fsProjId = #{fsprojid,jdbcType=VARCHAR}
  and fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

update tbauthority
set fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER}
where fsProgId = #{fsprogid,jdbcType=VARCHAR}
  and fsProjId = #{fsprojid,jdbcType=VARCHAR}
  and fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
