mapper.select:
select 

from tbauthoritydtl
where fsProgDtlId = #{fsprogdtlid,jdbcType=VARCHAR}
  and fsProgId = #{fsprogid,jdbcType=VARCHAR}
  and fsProjId = #{fsprojid,jdbcType=VARCHAR}
  and fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbauthoritydtl
where fsProgDtlId = #{fsprogdtlid,jdbcType=VARCHAR}
  and fsProgId = #{fsprogid,jdbcType=VARCHAR}
  and fsProjId = #{fsprojid,jdbcType=VARCHAR}
  and fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.update:
update tbauthoritydtl


    fiUsable = #{fiusable,jdbcType=INTEGER},


    fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},


    fiStatus = #{fistatus,jdbcType=INTEGER},


where fsProgDtlId = #{fsprogdtlid,jdbcType=VARCHAR}
  and fsProgId = #{fsprogid,jdbcType=VARCHAR}
  and fsProjId = #{fsprojid,jdbcType=VARCHAR}
  and fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

update tbauthoritydtl
set fiUsable = #{fiusable,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER}
where fsProgDtlId = #{fsprogdtlid,jdbcType=VARCHAR}
  and fsProgId = #{fsprogid,jdbcType=VARCHAR}
  and fsProjId = #{fsprojid,jdbcType=VARCHAR}
  and fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
