mapper.select:
select 

from tbbuygiftitem
where fiItemCd = #{fiitemcd,jdbcType=INTEGER}
  and fiOrderUintCd = #{fiorderuintcd,jdbcType=INTEGER}
  and fsBargainId = #{fsbargainid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbbuygiftitem
where fiItemCd = #{fiitemcd,jdbcType=INTEGER}
  and fiOrderUintCd = #{fiorderuintcd,jdbcType=INTEGER}
  and fsBargainId = #{fsbargainid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.insert:
insert into tbbuygiftitem (fiItemCd, fiOrderUintCd, fsBargainId, 
  fsShopGUID, fdBargainPrice, fdSaleQty, 
  fdSaleQty_gift, fiDiscountRate, fsUpdateTime, 
  fsUpdateUserId, fsUpdateUserName, fiStatus
  )
values (#{fiitemcd,jdbcType=INTEGER}, #{fiorderuintcd,jdbcType=INTEGER}, #{fsbargainid,jdbcType=VARCHAR}, 
  #{fsshopguid,jdbcType=VARCHAR}, #{fdbargainprice,jdbcType=DECIMAL}, #{fdsaleqty,jdbcType=DECIMAL}, 
  #{fdsaleqtyGift,jdbcType=DECIMAL}, #{fidiscountrate,jdbcType=INTEGER}, #{fsupdatetime,jdbcType=VARCHAR}, 
  #{fsupdateuserid,jdbcType=VARCHAR}, #{fsupdateusername,jdbcType=VARCHAR}, #{fistatus,jdbcType=INTEGER}
  )

mapper.update:
update tbbuygiftitem


    fdBargainPrice = #{fdbargainprice,jdbcType=DECIMAL},


    fdSaleQty = #{fdsaleqty,jdbcType=DECIMAL},


    fdSaleQty_gift = #{fdsaleqtyGift,jdbcType=DECIMAL},


    fiDiscountRate = #{fidiscountrate,jdbcType=INTEGER},


    fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},


    fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},


    fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR},


    fiStatus = #{fistatus,jdbcType=INTEGER},


where fiItemCd = #{fiitemcd,jdbcType=INTEGER}
  and fiOrderUintCd = #{fiorderuintcd,jdbcType=INTEGER}
  and fsBargainId = #{fsbargainid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

update tbbuygiftitem
set fdBargainPrice = #{fdbargainprice,jdbcType=DECIMAL},
  fdSaleQty = #{fdsaleqty,jdbcType=DECIMAL},
  fdSaleQty_gift = #{fdsaleqtyGift,jdbcType=DECIMAL},
  fiDiscountRate = #{fidiscountrate,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER}
where fiItemCd = #{fiitemcd,jdbcType=INTEGER}
  and fiOrderUintCd = #{fiorderuintcd,jdbcType=INTEGER}
  and fsBargainId = #{fsbargainid,jdbcType=VARCHAR}
