  and fscookid = #{fscookid,jdbcType=VARCHAR}

mapper.delete:
delete from tbcookbook
where fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fscookid = #{fscookid,jdbcType=VARCHAR}

mapper.insert:
insert into tbcookbook (fsShopGUID, fscookid, fscookname, 
  fdpricerange, fidecround, fistatus, 
  fsUpdateTime, fsUpdateUserId, fsUpdateUserName
  )
values (#{fsshopguid,jdbcType=VARCHAR}, #{fscookid,jdbcType=VARCHAR}, #{fscookname,jdbcType=VARCHAR}, 
  #{fdpricerange,jdbcType=DECIMAL}, #{fidecround,jdbcType=INTEGER}, #{fistatus,jdbcType=INTEGER}, 
  #{fsupdatetime,jdbcType=VARCHAR}, #{fsupdateuserid,jdbcType=VARCHAR}, #{fsupdateusername,jdbcType=VARCHAR}
  )

mapper.update:
update tbcookbook
set fscookname = #{fscookname,jdbcType=VARCHAR},
  fdpricerange = #{fdpricerange,jdbcType=DECIMAL},
  fidecround = #{fidecround,jdbcType=INTEGER},
  fistatus = #{fistatus,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR}
where fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fscookid = #{fscookid,jdbcType=VARCHAR}
