mapper.select:
select 

from tbcreditaccount
where fsCreditAccountId = #{fscreditaccountid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbcreditaccount
where fsCreditAccountId = #{fscreditaccountid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
