mapper.select:
select 

from tbdeliverer
where fsDelivererId = #{fsdelivererid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbdeliverer
where fsDelivererId = #{fsdelivererid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.insert:
insert into tbdeliverer (fsDelivererId, fsShopGUID, fsDelivererName, 
  fiDeliverertype, fsDeliverer, fsCellphone, 
  fdChange, fsNote, fiStatus, 
  fsUpdateTime, fsUpdateUserId, fsUpdateUserName
  )
values (#{fsdelivererid,jdbcType=VARCHAR}, #{fsshopguid,jdbcType=VARCHAR}, #{fsdeliverername,jdbcType=VARCHAR}, 
  #{fideliverertype,jdbcType=INTEGER}, #{fsdeliverer,jdbcType=VARCHAR}, #{fscellphone,jdbcType=VARCHAR}, 
  #{fdchange,jdbcType=DECIMAL}, #{fsnote,jdbcType=VARCHAR}, #{fistatus,jdbcType=INTEGER}, 
  #{fsupdatetime,jdbcType=VARCHAR}, #{fsupdateuserid,jdbcType=VARCHAR}, #{fsupdateusername,jdbcType=VARCHAR}
  )
