mapper.select:
select 

,

from tbhostexternal
where fiCls = #{ficls,jdbcType=INTEGER}
  and fsHostId = #{fshostid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbhostexternal
where fiCls = #{ficls,jdbcType=INTEGER}
  and fsHostId = #{fshostid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
