mapper.select:
select 

from tbhostver
where fsFileName = #{fsfilename,jdbcType=VARCHAR}
  and fsHost = #{fshost,jdbcType=VARCHAR}

mapper.delete:
delete from tbhostver
where fsFileName = #{fsfilename,jdbcType=VARCHAR}
  and fsHost = #{fshost,jdbcType=VARCHAR}

mapper.update:
update tbhostver


    fsVer = #{fsver,jdbcType=VARCHAR},


    fsDateTime = #{fsdatetime,jdbcType=VARCHAR},


where fsFileName = #{fsfilename,jdbcType=VARCHAR}
  and fsHost = #{fshost,jdbcType=VARCHAR}

update tbhostver
set fsVer = #{fsver,jdbcType=VARCHAR},
  fsDateTime = #{fsdatetime,jdbcType=VARCHAR}
where fsFileName = #{fsfilename,jdbcType=VARCHAR}
  and fsHost = #{fshost,jdbcType=VARCHAR}
