mapper.select:
select 

from tbmarea
where fsMAreaId = #{fsmareaid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbmarea
where fsMAreaId = #{fsmareaid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.insert:
insert into tbmarea (fsMAreaId, fsShopGUID, fsMAreaName, 
  fiStatus, fsUpdateTime, fsUpdateUserId, 
  fsUpdateUserName, fsPrinterName, fiSortOrder
  )
values (#{fsmareaid,jdbcType=VARCHAR}, #{fsshopguid,jdbcType=VARCHAR}, #{fsmareaname,jdbcType=VARCHAR}, 
  #{fistatus,jdbcType=INTEGER}, #{fsupdatetime,jdbcType=VARCHAR}, #{fsupdateuserid,jdbcType=VARCHAR}, 
  #{fsupdateusername,jdbcType=VARCHAR}, #{fsprintername,jdbcType=VARCHAR}, #{fisortorder,jdbcType=INTEGER}
  )

mapper.update:
update tbmarea
set fsMAreaName = #{fsmareaname,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR},
  fsPrinterName = #{fsprintername,jdbcType=VARCHAR},
  fiSortOrder = #{fisortorder,jdbcType=INTEGER}
where fsMAreaId = #{fsmareaid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
