mapper.select:
select 

from tbmenuingredientgp
where fiItemGroupId = #{fiitemgroupid,jdbcType=INTEGER}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbmenuingredientgp
where fiItemGroupId = #{fiitemgroupid,jdbcType=INTEGER}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.update:
update tbmenuingredientgp
set fsGroupName = #{fsgroupname,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR}
where fiItemGroupId = #{fiitemgroupid,jdbcType=INTEGER}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
