mapper.select:
select 

from tbmenuitemsetside
where fiItemCd_M = #{fiitemcdM,jdbcType=INTEGER}
  and fiSetFoodCd = #{fisetfoodcd,jdbcType=INTEGER}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbmenuitemsetside
where fiItemCd_M = #{fiitemcdM,jdbcType=INTEGER}
  and fiSetFoodCd = #{fisetfoodcd,jdbcType=INTEGER}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
