mapper.select:
select 

from tbmsection
where fsMSectionId = #{fsmsectionid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbmsection
where fsMSectionId = #{fsmsectionid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.insert:
insert into tbmsection (fsMSectionId, fsShopGUID, fsMSectionName, 
  fsBeginTime, fsEndTime, fiStatus, 
  fsUpdateTime, fsUpdateUserId, fsUpdateUserName
  )
values (#{fsmsectionid,jdbcType=VARCHAR}, #{fsshopguid,jdbcType=VARCHAR}, #{fsmsectionname,jdbcType=VARCHAR}, 
  #{fsbegintime,jdbcType=VARCHAR}, #{fsendtime,jdbcType=VARCHAR}, #{fistatus,jdbcType=INTEGER}, 
  #{fsupdatetime,jdbcType=VARCHAR}, #{fsupdateuserid,jdbcType=VARCHAR}, #{fsupdateusername,jdbcType=VARCHAR}
  )

mapper.update:
update tbmsection
set fsMSectionName = #{fsmsectionname,jdbcType=VARCHAR},
  fsBeginTime = #{fsbegintime,jdbcType=VARCHAR},
  fsEndTime = #{fsendtime,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR}
where fsMSectionId = #{fsmsectionid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
