mapper.select:
select 

from tbmtableste
where fsMTableSteId = #{fsmtablesteid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbmtableste
where fsMTableSteId = #{fsmtablesteid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.update:
update tbmtableste
set fsMTableSteName = #{fsmtablestename,jdbcType=VARCHAR},
  fsColor = #{fscolor,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR}
where fsMTableSteId = #{fsmtablesteid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
