mapper.select:
select 

from tbpaymenttype
where fsPaymentTypeId = #{fspaymenttypeid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbpaymenttype
where fsPaymentTypeId = #{fspaymenttypeid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.insert:
insert into tbpaymenttype (fsPaymentTypeId, fsShopGUID, fsPaymentTypeName, 
  fsNote, fiStatus, fiDataKind, 
  fsUpdateTime, fsUpdateUserId, fsUpdateUserName
  )
values (#{fspaymenttypeid,jdbcType=VARCHAR}, #{fsshopguid,jdbcType=VARCHAR}, #{fspaymenttypename,jdbcType=VARCHAR}, 
  #{fsnote,jdbcType=VARCHAR}, #{fistatus,jdbcType=INTEGER}, #{fidatakind,jdbcType=INTEGER}, 
  #{fsupdatetime,jdbcType=VARCHAR}, #{fsupdateuserid,jdbcType=VARCHAR}, #{fsupdateusername,jdbcType=VARCHAR}
  )

mapper.update:
update tbpaymenttype
set fsPaymentTypeName = #{fspaymenttypename,jdbcType=VARCHAR},
  fsNote = #{fsnote,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER},
  fiDataKind = #{fidatakind,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR}
where fsPaymentTypeId = #{fspaymenttypeid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
