mapper.select:
select 

from tbprogdtl
where fsProgDtlId = #{fsprogdtlid,jdbcType=VARCHAR}
  and fsProgId = #{fsprogid,jdbcType=VARCHAR}

mapper.delete:
delete from tbprogdtl
where fsProgDtlId = #{fsprogdtlid,jdbcType=VARCHAR}
  and fsProgId = #{fsprogid,jdbcType=VARCHAR}

mapper.update:
update tbprogdtl
set fsProglName = #{fsproglname,jdbcType=VARCHAR},
  fiSortOrder = #{fisortorder,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR}
where fsProgDtlId = #{fsprogdtlid,jdbcType=VARCHAR}
  and fsProgId = #{fsprogid,jdbcType=VARCHAR}
