mapper.select:
select 

from tbpublish
where fiId = #{fiid,jdbcType=BIGINT}
  and fiSoftId = #{fisoftid,jdbcType=BIGINT}
  and fsVersion = #{fsversion,jdbcType=VARCHAR}

mapper.delete:
delete from tbpublish
where fiId = #{fiid,jdbcType=BIGINT}
  and fiSoftId = #{fisoftid,jdbcType=BIGINT}
  and fsVersion = #{fsversion,jdbcType=VARCHAR}
