mapper.select:
select 

from tbsellhurryitem
where fiHurrySeq = #{fihurryseq,jdbcType=INTEGER}
  and fsSellNo = #{fssellno,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsSourceSeq = #{fssourceseq,jdbcType=VARCHAR}

mapper.delete:
delete from tbsellhurryitem
where fiHurrySeq = #{fihurryseq,jdbcType=INTEGER}
  and fsSellNo = #{fssellno,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsSourceSeq = #{fssourceseq,jdbcType=VARCHAR}

mapper.insert:
