  fsSourceSeq, fsCreateTime, fsCreateUserName
  )
values (#{fihurryseq,jdbcType=INTEGER}, #{fssellno,jdbcType=VARCHAR}, #{fsshopguid,jdbcType=VARCHAR}, 
  #{fssourceseq,jdbcType=VARCHAR}, #{fscreatetime,jdbcType=VARCHAR}, #{fscreateusername,jdbcType=VARCHAR}
  )

mapper.update:
update tbsellhurryitem


    fsCreateTime = #{fscreatetime,jdbcType=VARCHAR},


    fsCreateUserName = #{fscreateusername,jdbcType=VARCHAR},


