mapper.select:
select 

from tbsellorder
where fiOrderSeq = #{fiorderseq,jdbcType=INTEGER}
  and fsSellNo = #{fssellno,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbsellorder
where fiOrderSeq = #{fiorderseq,jdbcType=INTEGER}
  and fsSellNo = #{fssellno,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
