mapper.select:
select 

from tbshopgroupdtl
where fiShopGroupId = #{fishopgroupid,jdbcType=INTEGER}
  and fsCompanyGUID = #{fscompanyguid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbshopgroupdtl
where fiShopGroupId = #{fishopgroupid,jdbcType=INTEGER}
  and fsCompanyGUID = #{fscompanyguid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.update:
update tbshopgroupdtl


    fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},


    fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},


    fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR},


    fiStatus = #{fistatus,jdbcType=INTEGER},


where fiShopGroupId = #{fishopgroupid,jdbcType=INTEGER}
  and fsCompanyGUID = #{fscompanyguid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

update tbshopgroupdtl
set fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER}
where fiShopGroupId = #{fishopgroupid,jdbcType=INTEGER}
  and fsCompanyGUID = #{fscompanyguid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
