mapper.select:
select 

from tbuserrole
where fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsUserId = #{fsuserid,jdbcType=VARCHAR}

mapper.delete:
delete from tbuserrole
where fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsUserId = #{fsuserid,jdbcType=VARCHAR}

mapper.update:
update tbuserrole


    fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},


    fiStatus = #{fistatus,jdbcType=INTEGER},


where fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsUserId = #{fsuserid,jdbcType=VARCHAR}

update tbuserrole
set fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER}
where fsRoleId = #{fsroleid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsUserId = #{fsuserid,jdbcType=VARCHAR}
