mapper.select:
select 

from tbusershopauthority
where fsCompanyGUID = #{fscompanyguid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsUserId = #{fsuserid,jdbcType=VARCHAR}

mapper.delete:
delete from tbusershopauthority
where fsCompanyGUID = #{fscompanyguid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsUserId = #{fsuserid,jdbcType=VARCHAR}

mapper.update:
update tbusershopauthority


    fsEnabled = #{fsenabled,jdbcType=INTEGER},


    fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},


    fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},


    fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR},


where fsCompanyGUID = #{fscompanyguid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsUserId = #{fsuserid,jdbcType=VARCHAR}

update tbusershopauthority
set fsEnabled = #{fsenabled,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR}
where fsCompanyGUID = #{fscompanyguid,jdbcType=VARCHAR}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsUserId = #{fsuserid,jdbcType=VARCHAR}
