mapper.select:
select 

from tbvipdiscount
where fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsVIPLvlId = #{fsviplvlid,jdbcType=VARCHAR}

mapper.delete:
delete from tbvipdiscount
where fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsVIPLvlId = #{fsviplvlid,jdbcType=VARCHAR}

mapper.update:
update tbvipdiscount
set fiDiscountType = #{fidiscounttype,jdbcType=INTEGER},
  fsDiscountId = #{fsdiscountid,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR}
where fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
  and fsVIPLvlId = #{fsviplvlid,jdbcType=VARCHAR}
