  #{fsnote,jdbcType=VARCHAR}, #{fisortorder,jdbcType=INTEGER}, #{fistatus,jdbcType=INTEGER}, 
  #{fsupdatetime,jdbcType=VARCHAR}, #{fsupdateuserid,jdbcType=VARCHAR}, #{fsupdateusername,jdbcType=VARCHAR}
  )

mapper.update:
update tbviplvl
