mapper.select:
select 

from tbwordhouse
where fiId = #{fiid,jdbcType=INTEGER}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.delete:
delete from tbwordhouse
where fiId = #{fiid,jdbcType=INTEGER}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}

mapper.insert:
insert into tbwordhouse (fiId, fsShopGUID, fsFieldItem, 
  fsWord, fiSortOrder, fsUpdateTime, 
  fsUpdateUserId, fsUpdateUserName, fiStatus
  )
values (#{fiid,jdbcType=INTEGER}, #{fsshopguid,jdbcType=VARCHAR}, #{fsfielditem,jdbcType=VARCHAR}, 
  #{fsword,jdbcType=VARCHAR}, #{fisortorder,jdbcType=INTEGER}, #{fsupdatetime,jdbcType=VARCHAR}, 
  #{fsupdateuserid,jdbcType=VARCHAR}, #{fsupdateusername,jdbcType=VARCHAR}, #{fistatus,jdbcType=INTEGER}
  )

mapper.update:
update tbwordhouse
set fsFieldItem = #{fsfielditem,jdbcType=VARCHAR},
  fsWord = #{fsword,jdbcType=VARCHAR},
  fiSortOrder = #{fisortorder,jdbcType=INTEGER},
  fsUpdateTime = #{fsupdatetime,jdbcType=VARCHAR},
  fsUpdateUserId = #{fsupdateuserid,jdbcType=VARCHAR},
  fsUpdateUserName = #{fsupdateusername,jdbcType=VARCHAR},
  fiStatus = #{fistatus,jdbcType=INTEGER}
where fiId = #{fiid,jdbcType=INTEGER}
  and fsShopGUID = #{fsshopguid,jdbcType=VARCHAR}
